mapper.select:
/* BatchMapper.selectChgDrmncyMemList ([휴면회원 전환] 처리 필요한 리스트 조회) */
SELECT (row_number() over(order by tub.last_lgn_dt ASC, tub.user_id )) as rownum , tub.user_id
, TO_CHAR( COALESCE(tub.last_lgn_dt, tub.crt_dt) , 'YYYY-MM-DD' ) -- 마지막 로그인(없으면 생성일) FROM tb_user_bas tub WHERE tub.user_stt_cd = 'USC020' /* 사용자상태코드가 [정상] 인것만*/ AND ( TO_CHAR( COALESCE(tub.last_lgn_dt, tub.crt_dt) , 'YYYY-MM-DD' ) <= TO_CHAR( (now() - '1 year'::interval - '1 day'::interval) , 'YYYY-MM-DD' ) ) /* 마지막 로그인 시간이 (1년)전 보다 적으면 */ ORDER BY rownum desc

/* BatchMapper.selectBeforeDrmncyMemList ([휴면회원 전환 30일전 알림 메일] 처리 필요한 리스트 조회) */
SELECT (row_number() over(order by tub.last_lgn_dt ASC, tub.user_id )) as rownum , tub.user_id , tub.encpt_eml_addr_val , TO_CHAR( now() + '30 day'::interval , 'YYYY-MM-DD' ) as e_drmncy_day --휴면예정일
, TO_CHAR( COALESCE(tub.last_lgn_dt, tub.crt_dt) , 'YYYY-MM-DD' ) AS rast_lgn_date -- 마지막 로그인(없으면 생성일) FROM tb_user_bas tub WHERE tub.user_stt_cd = 'USC020' /* 사용자상태코드가 [정상] 인것만*/ AND ( TO_CHAR( COALESCE(tub.last_lgn_dt, tub.crt_dt) , 'YYYY-MM-DD' ) = TO_CHAR( (now() - '1 year'::interval + '30 day'::interval - '1 day'::interval) , 'YYYY-MM-DD' ) ) /* 마지막 로그인 시간이 (1년 - 30일)전 */ ORDER BY rownum desc

/* BatchMapper.selectInstEntGrpClctAgreList ([기업그룹수집동의여부 기관에게 알림 메일] 기관에 보낼 기업그룹수집동의여부리스트 조회) */
SELECT tegcam.inst_id , tegcam.ent_grp_sn
, teb.brno --사업자등록번호 , teb.ent_nm --기업명 , tegb.ent_grp_mng_no --기업그룹ID , tegb.ent_grp_nm --기업그룹명 , tegcam.info_pvsn_agre_yn --수집동의여부 , TO_CHAR(tegcam.last_info_pvsn_agre_yn_dt, 'YYYY-MM-DD HH24:MI:SS') as last_info_pvsn_agre_yn_dt_fmt --수집동의여부일시 FROM tb_ent_grp_clct_agre_mng tegcam LEFT OUTER JOIN tb_ent_grp_bas tegb ON tegb.use_yn = 'Y' AND tegb.ent_grp_sn = tegcam.ent_grp_sn LEFT OUTER JOIN tb_ent_bas teb ON teb.use_yn = 'Y' AND teb.brno = tegb.brno WHERE tegcam.inst_id = #{instId} AND TO_CHAR(tegcam.last_info_pvsn_agre_yn_dt , 'YYYY-MM-DD') = TO_CHAR( NOW() - '1 day'::interval , 'YYYY-MM-DD') /* 수집동의여부일시가 (1일)전 */ ORDER BY info_pvsn_agre_yn DESC, tegcam.last_info_pvsn_agre_yn_dt ASC

mapper.insert:
/* BatchMapper.insertBtchExcnHist (배치실행이력 등록) */

INSERT INTO tb_btch_excn_hist (
	btch_id
	, btch_excn_dt
	, btch_rslt_cd
	, btch_rslt_cn
) VALUES (
	#{btchId}
	, now()
	, #{btchRsltCd}
	, #{btchRsltCn}
)
