mapper.sql:
/* SQL : [플랫폼 서비스] > [내 손안의 시험인증] > [나의 시험인증 정보조회 : 인증] 리스트 쿼리 */
SELECT mycr.inst_id --기관아이디 , (SELECT CONCAT (pcib.inst_nm, '(' , pcib.inst_eng_nm, ')' ) FROM pt_co_inst_bas pcib WHERE pcib.use_yn ='Y' AND pcib.inst_id = mycr.inst_id LIMIT 1) AS inst_ko_eng_nm --기관명(국문, 영문) , mycr.mdl_nm --모델명 , mycr.prdt_nm --제품명 , mycr.cert_stt_cd --인증상태코드 , (SELECT pcicdc.dtl_cd_nm FROM pt_co_intgr_comn_dtl_cd pcicdc WHERE pcicdc.use_yn ='Y' AND pcicdc.grp_cd='ATC007' AND pcicdc.dtl_cd = mycr.cert_stt_cd LIMIT 1) AS cert_stt_nm --인증상태명 , mycr.cert_no --인증번호 , mycr.inst_cert_idntfc_val --통합접수번호 , mycr.cert_ymd --인증일자 , fn_date_fmt(mycr.cert_ymd, 'YYYY-MM-DD') AS cert_ymd_fmt --인증일자_포맷팅
, coalesce( (SELECT 'Y' FROM tb_cert_adit_info_mng tcaim WHERE tcaim.inst_id = mycr.inst_id AND tcaim.cert_no = mycr.cert_no LIMIT 1), 'N') AS have_cert_adit_info --추가정보 등록여부 , coalesce( (SELECT 'Y' FROM tb_file_mng tfm WHERE tfm.del_yn = 'N' AND tfm.file_div_nm = 'qr_img' AND tfm.ref_div_val = CONCAT(mycr.inst_id,'||',mycr.cert_no) LIMIT 1) , 'N') AS have_qr_img_file --QR코드 생성여부
, (CASE WHEN mycr.cert_ymd = '0' THEN '00000000' ELSE fn_date_fmt(to_char(to_date(mycr.cert_ymd, 'YYYYMMDD') + interval '5 year', 'YYYYMMDD'), 'YYYY-MM-DD') END) AS cert_end_ymd_fmt , (CASE WHEN mycr.cert_ymd = '0' THEN '0' ELSE date_part('day', now() - (to_date(mycr.cert_ymd, 'YYYYMMDD') + interval '5 year')) END) AS cert_end_minus_day FROM ( SELECT prcb.inst_id --기관아이디 , prcb.mdl_nm --모델명 , prcb.prdt_nm --제품명 , prcb.cert_stt_cd --인증상태코드 , prcb.cert_no --인증번호 , prcb.inst_cert_idntfc_val --통합접수번호 , prcb.cert_ymd --인증일자 FROM pt_rf_cert_bas prcb
ORDER BY prcb.cert_ymd DESC, prcb.clct_file_ymd ASC, prcb.cert_no ASC
OFFSET #{startRow} LIMIT #{endRow}
) AS mycr

mapper.select:
/* MyDataMapper.selectMyCertImgList ([플랫폼 서비스] > [내 손안의 시험인증] > [나의 시험인증 정보조회 : 인증] 이미지 리스트 조회) */

SELECT
	prcit.cert_img_file_nm --인증이미지파일명
FROM pt_rf_cert_img_txn prcit
WHERE UPPER(prcit.inst_id) = #{targetInstId} AND prcit.cert_no = #{targetCertNo}
ORDER BY prcit.file_nm

mapper.insert:
/* MyDataMapper.mergeCertAditInfoMng ([인증서추가정보관리] 등록,수정) */

WITH upsert AS
				(
					UPDATE tb_cert_adit_info_mng
					SET
						adit_info_nm = #{aditInfoNm}
						, adit_info_cn = #{aditInfoCn}
						, mdfcn_dt = now()
						, mdfr_id = #{crtrId}
					WHERE inst_id = #{instId} AND cert_no = #{certNo}
					RETURNING *
				)
INSERT INTO tb_cert_adit_info_mng
				(
					inst_id
					, cert_no
					, adit_info_nm
					, adit_info_cn
					, crt_dt
					, crtr_id
				)
SELECT
	#{instId}
	, #{certNo}
	, #{aditInfoNm}
	, #{aditInfoCn}
	, now()
	, #{crtrId}
WHERE NOT EXISTS(SELECT * FROM upsert);

mapper.sql:
/* SQL : [플랫폼 서비스] > [내 손안의 시험인증] > [나의 시험인증 정보조회 : 성적서] 리스트 쿼리 */
SELECT myrr.inst_id --기관아이디 , (SELECT CONCAT (pcib.inst_nm, '(' , pcib.inst_eng_nm, ')' ) FROM pt_co_inst_bas pcib WHERE pcib.use_yn ='Y' AND pcib.inst_id = myrr.inst_id LIMIT 1) AS inst_ko_eng_nm --기관명(국문, 영문) , myrr.inst_rcpt_no --기관접수번호 , myrr.rprt_no --성적서번호 , myrr.rprt_issu_ymd --성적서발급일자 , fn_date_fmt(myrr.rprt_issu_ymd, 'YYYY-MM-DD') AS rprt_issu_ymd_fmt --성적서발급일자_포맷팅 , myrr.intgr_gnrlz_jgmt_rslt_cd --통합종합판정결과코드 , (SELECT pcicdc.dtl_cd_nm FROM pt_co_intgr_comn_dtl_cd pcicdc WHERE pcicdc.use_yn ='Y' AND pcicdc.grp_cd='ATC005' AND pcicdc.dtl_cd = myrr.intgr_gnrlz_jgmt_rslt_cd LIMIT 1) AS intgr_gnrlz_jgmt_rslt_nm --통합종합판정결과명 , COALESCE ( (SELECT STRING_AGG(lgclf.std_lgclf_nm, ',') FROM pt_rf_pdctg_bas pdctg /* 품목기본 */ INNER JOIN pt_co_indst_std_lgclf_cd lgclf ON pdctg.std_lgclf_cd = lgclf.std_lgclf_cd AND lgclf.use_yn = 'Y' WHERE myrr.inst_id = pdctg.inst_id AND myrr.inst_rcpt_no = pdctg.inst_rcpt_no AND myrr.rprt_no = pdctg.rprt_no ), '' ) AS std_lgclf_nm --제품대분류명 , COALESCE ( (SELECT STRING_AGG(mlclf_cd.std_mlclf_nm, ',') FROM pt_rf_pdctg_bas pdctg /* 품목기본 */ INNER JOIN pt_co_indst_std_mlclf_cd mlclf_cd ON pdctg.std_lgclf_cd = mlclf_cd.std_lgclf_cd AND pdctg.std_mlclf_cd = mlclf_cd.std_mlclf_cd AND mlclf_cd.use_yn = 'Y' WHERE myrr.inst_id = pdctg.inst_id AND myrr.inst_rcpt_no = pdctg.inst_rcpt_no AND myrr.rprt_no = pdctg.rprt_no ), '' ) AS std_mlclf_nm --제품중분류명 , COALESCE ( (SELECT string_agg(pdctg.inst_pdctg_nm, ',') FROM pt_rf_pdctg_bas pdctg /* 품목기본 */ WHERE myrr.inst_id = pdctg.inst_id AND myrr.inst_rcpt_no = pdctg.inst_rcpt_no AND myrr.rprt_no = pdctg.rprt_no ), '' ) AS inst_pdctg_nm --품목명 FROM ( SELECT rprt.inst_id --기관아이디 , rprt.inst_rcpt_no --기관접수번호 , rprt.rprt_no --성적서번호 , rprt.rprt_issu_ymd --성적서발급일자 , rprt.intgr_gnrlz_jgmt_rslt_cd --통합종합판정결과코드
ORDER BY rprt.rprt_issu_ymd DESC, rprt.inst_rcpt_no ASC
OFFSET #{startRow} LIMIT #{endRow}
) AS myrr

mapper.select:
/* MyDataMapper.selectMyRprtCount ([플랫폼 서비스] > [내 손안의 시험인증] > [나의 시험인증 정보조회 : 성적서] 리스트 개수 조회) */

SELECT
	count(1) as count

/* MyDataMapper.selectMyRprtDetail ([플랫폼 서비스] > [내 손안의 시험인증] > [나의 시험인증 정보조회 : 성적서] 상세 조회) */
SELECT prrb.inst_id --기관아이디 , (SELECT CONCAT (pcib.inst_nm, '(' , pcib.inst_eng_nm, ')' ) FROM pt_co_inst_bas pcib WHERE pcib.use_yn ='Y' AND pcib.inst_id = prrb.inst_id LIMIT 1) AS inst_ko_eng_nm --기관명(국문, 영문) , prrb.inst_rcpt_no --기관접수번호 , prrb.rprt_no --성적서번호 , prrb.rprt_issu_ymd --발급일자 , prrb.intgr_rprt_prpos_div_cd --통합성적서용도구분코드 , prrb.inst_rprt_prpos_div_nm --기관성적서용도구분명 , prrb.rprt_issu_tms_no --성적서발급회차번호 , prrb.intgr_gnrlz_jgmt_rslt_cd --통합종합판정결과코드 , (SELECT pcicdc.dtl_cd_nm FROM pt_co_intgr_comn_dtl_cd pcicdc WHERE pcicdc.use_yn ='Y' AND pcicdc.grp_cd='ATC005' AND pcicdc.dtl_cd = prrb.intgr_gnrlz_jgmt_rslt_cd LIMIT 1) AS intgr_gnrlz_jgmt_rslt_nm --통합종합판정결과명 , prrb.rmk_cn --비고
, rprt_pdctg.rcpt_co_nm , rprt_pdctg.sbmsn_co_nm
FROM pt_rf_rprt_bas prrb /*성적서 기본*/ INNER JOIN ( /* 볼 권한 없으면 데이터 안나오게 하기 위해 join */ SELECT U.inst_id , U.inst_rcpt_no , U.rprt_no , U.rcpt_co_nm , U.sbmsn_co_nm FROM ( /* [검색:입장구분:전체] or [검색:입장구분:신청] */ /***** 접수사업자등록번호로 조회한 접수와 품목 *****/ SELECT rcpt.inst_id , rcpt.inst_rcpt_no , rcpt.rprt_no , rcpt.rcpt_co_nm , rcpt.sbmsn_co_nm FROM pt_rf_rcpt_bas rcpt /* 접수기본 */ WHERE rcpt.rcpt_co_brno = #{brno} /* 접수회사사업자등록번호 = 로그인한 사용자 사업자번호 */ AND rcpt.grp_id = #{entGrpMngNo} /* 그룹아이디 = 로그인한 사용자 그룹관리번호 */
/***** 접수사업자등록번호로 조회한 접수와 품목 끝 *****/
UNION ALL /* [검색:입장구분:전체] */
/* [검색:입장구분:전체] or [검색:입장구분:제출] */ /***** 제출사업자등록번호로 조회한 접수와 품목 *****/ SELECT rcpt.inst_id , rcpt.inst_rcpt_no , rcpt.rprt_no , rcpt.rcpt_co_nm , rcpt.sbmsn_co_nm FROM pt_rf_rcpt_bas rcpt INNER JOIN tb_ent_grp_clct_agre_mng mng /* 기업그룹수집동의관리 */ ON rcpt.inst_id = mng.inst_id AND rcpt.rcpt_co_brno = mng.brno AND rcpt.grp_id = (case when mng.ent_grp_mng_no = '9999999999' then '_' else mng.ent_grp_mng_no end) AND mng.sbmsn_co_rls_agre_yn = 'Y' WHERE rcpt.sbmsn_co_brno = #{brno} /* 제출회사사업자등록번호 = 로그인한 사용자 사업자번호 */
/***** 제출사업자등록번호로 조회한 접수와 품목 끝 *****/ ) U GROUP BY U.inst_id, U.inst_rcpt_no, U.rprt_no, U.rcpt_co_nm , U.sbmsn_co_nm ) rprt_pdctg /* 접수,품목 */ ON rprt_pdctg.inst_id = prrb.inst_id AND rprt_pdctg.inst_rcpt_no = prrb.inst_rcpt_no AND rprt_pdctg.rprt_no = prrb.rprt_no WHERE UPPER(prrb.inst_id) = #{targetInstId} AND prrb.inst_rcpt_no = #{targetInstRcptNo} AND prrb.rprt_no = #{targetRprtNo} LIMIT 1

/* MyDataMapper.selectMyRprtInstSampleList ([플랫폼 서비스] > [내 손안의 시험인증] > [나의 시험인증 정보조회 : 성적서] [##시료 정보##] 리스트 조회) */
SELECT sample.inst_id --기관아이디 , sample.inst_rcpt_no --기관접수번호 , sample.rprt_no --성적서번호 , sample.inst_pdctg_cd --기관품목코드 , sample.inst_sample_no --기관시료번호 -- , sample.inst_sample_nm --기관시료명 , MAX(sample.inst_sample_nm) AS inst_sample_nm /* 기관시료명-중복제거임시*/ FROM pt_rf_sample_bas sample /* 시료기본 */ INNER JOIN pt_rf_pdctg_bas pdctg /* 품목기본 */ ON sample.inst_id = pdctg.inst_id AND sample.inst_rcpt_no = pdctg.inst_rcpt_no AND sample.rprt_no = pdctg.rprt_no AND sample.inst_pdctg_cd = pdctg.inst_pdctg_cd INNER JOIN pt_rf_rcpt_bas rcpt /* 접수기본 */ ON rcpt.inst_id = pdctg.inst_id AND rcpt.inst_rcpt_no = pdctg.inst_rcpt_no AND rcpt.rprt_no = pdctg.rprt_no WHERE UPPER(sample.inst_id) = #{targetInstId} /*기관아이디*/ AND sample.inst_rcpt_no = #{targetInstRcptNo} /*기관접수번호*/ AND sample.rprt_no = #{targetRprtNo} /*성적서번호*/ AND sample.inst_pdctg_cd = #{targetInstPdctgCd} /*기관품목코드*/
GROUP BY sample.inst_id, sample.inst_rcpt_no, sample.rprt_no, sample.inst_pdctg_cd, sample.inst_sample_no/* groupby-중복제거임시*/ ORDER BY sample.inst_sample_no ASC

mapper.insert:
