/* MyDataMapper.mergeEntGrpClctAgreMng ([기업그룹수집동의관리] 등록,수정) */

WITH updata AS
				(
					UPDATE tb_ent_grp_clct_agre_mng
					SET

						info_pvsn_agre_yn = #{infoPvsnAgreYn}
						, last_info_pvsn_agre_yn_dt = now()


						sbmsn_co_rls_agre_yn = #{sbmsnCoRlsAgreYn}
						, last_sbmsn_co_rls_agre_yn_dt = now()

						, mdfcn_dt = now()
						, mdfr_id = #{crtrId}
					WHERE inst_id = #{instId} AND ent_grp_sn = #{entGrpSn}
					RETURNING *
				)
INSERT INTO tb_ent_grp_clct_agre_mng
				(
					ent_grp_sn
					, inst_id

					, info_pvsn_agre_yn
					, last_info_pvsn_agre_yn_dt


					, sbmsn_co_rls_agre_yn
					, last_sbmsn_co_rls_agre_yn_dt

					, crt_dt
					, crtr_id
					, brno
					, ent_grp_mng_no
				)
SELECT
	#{entGrpSn}
	, #{instId}

	, #{infoPvsnAgreYn}
	, now()


	, #{sbmsnCoRlsAgreYn}
	, now()

	, now()
	, #{crtrId}
	, #{brno}
	, #{entGrpMngNo}
WHERE NOT EXISTS(SELECT * FROM updata);

mapper.select:
/* MyDataMapper.selectEntGrpClctAgreData ( [기업그룹수집동의관리] + [사업자정보] 데이터 조회(Map으로 반환) ) */
SELECT tegcam.inst_id , tegcam.ent_grp_sn
, teb.brno --사업자등록번호 , teb.ent_nm --기업명 , tegb.ent_grp_mng_no --기업그룹ID , tegb.ent_grp_nm --기업그룹명 , tegcam.info_pvsn_agre_yn --수집동의여부 , TO_CHAR(tegcam.last_info_pvsn_agre_yn_dt, 'YYYYMMDDHH24MISS') as crt_dt_fmt --수집동의여부일시 FROM tb_ent_grp_clct_agre_mng tegcam LEFT OUTER JOIN tb_ent_grp_bas tegb ON tegb.use_yn = 'Y' AND tegb.ent_grp_sn = tegcam.ent_grp_sn LEFT OUTER JOIN tb_ent_bas teb ON teb.use_yn = 'Y' AND teb.brno = tegb.brno WHERE tegcam.inst_id = #{targetInstId} AND tegcam.ent_grp_sn = #{entGrpSn} LIMIT 1

mapper.update:
/* MyDataMapper.updatePtRfRcptBasNonIdenti ([접수기본 : pt_rf_rcpt_bas] 비식별처리 UPDATE(*로 처리)) */
UPDATE pt_rf_rcpt_bas SET rcpt_co_nm = '*' , rcpt_co_brno = '*' , rcpt_co_rprsv_nm = '*' , rcpt_co_telno = '*' , rcpt_co_addr = '*' , grp_id = '*' , grp_nm = '*' , sbmsn_co_nm = '*' , sbmsn_co_brno = '*' , sbmsn_co_rprsv_nm = '*' , sbmsn_co_telno = '*' , sbmsn_co_addr = '*' , agent_co_nm = '*' , agent_co_brno = '*' , agent_co_rprsv_nm = '*' , agent_co_telno = '*' , agent_co_addr = '*'
WHERE (inst_id = #{clctAgreInstId}) AND rcpt_co_brno = #{brno} /* 접수회사사업자등록번호 = 로그인한 사용자 사업자번호 */ AND grp_id = #{entGrpMngNoDefault} /* 그룹아이디 = 로그인한 사용자 그룹관리번호 */
